import Foundation
import AppKit

struct AlphaBadge {

	//alpha_badge_dark.png
	fileprivate static let alphaDark = ""
	//alpha_badge_light.png
	fileprivate static let alphaLight = ""

	static var dark: NSImage = {
		let data = Data(base64Encoded: alphaDark)!
		return NSImage(data: data)!
	}()
	
	static var light: NSImage = {
		let data = Data(base64Encoded: alphaLight)!
		return NSImage(data: data)!
	}()
	
	static func generate() {
		do {
			let text = try AssetGenerator.generate(from: "AlphaDark.png")
			print("Alpha, dark".bold.lightWhite)
			print(text.white)
		} catch let error {
			print("Failed to generate dark Alpha asset".lightRed)
			print("\(error)".red)
		}
		print("")
		do {
			let text = try AssetGenerator.generate(from: "AlphaLight.png")
			print("Alpha, light".bold.lightWhite)
			print(text.white)
		} catch let error {
			print("Failed to generate light Alpha asset".lightRed)
			print("\(error)".red)
		}
		print("")
	}

}
