import Foundation
import AppKit

struct BetaBadge {

	//beta_badge_dark.png
	fileprivate static let betaDark = ""
	//beta_badge_light.png
	fileprivate static let betaLight = ""

	static var dark: NSImage = {
		let data = Data(base64Encoded: betaDark)!
		return NSImage(data: data)!
	}()
	
	static var light: NSImage = {
		let data = Data(base64Encoded: betaLight)!
		return NSImage(data: data)!
	}()
	
	static func generate() {
		do {
			let text = try AssetGenerator.generate(from: "BetaDark.png")
			print("Beta, dark".bold.lightWhite)
			print(text.white)
		} catch let error {
			print("Failed to generate dark Beta asset".lightRed)
			print("\(error)".red)
		}
		print("")
		do {
			let text = try AssetGenerator.generate(from: "BetaLight.png")
			print("Beta, light".bold.lightWhite)
			print(text.white)
		} catch let error {
			print("Failed to generate light Beta asset".lightRed)
			print("\(error)".red)
		}
		print("")
	}

}
