import Foundation
import AppKit
import Rainbow

struct ReleaseBadge {

	//beta_badge_dark.png
	fileprivate static let betaDark = ""
	//beta_badge_light.png
	fileprivate static let betaLight = ""

	static var dark: NSImage = {
		let data = Data(base64Encoded: betaDark)!
		return NSImage(data: data)!
	}()
	
	static var light: NSImage = {
		let data = Data(base64Encoded: betaLight)!
		return NSImage(data: data)!
	}()
	
	static func generate() {
		do {
			let text = try AssetGenerator.generate(from: "ReleaseDark.png")
			print("Release, dark".bold.lightWhite)
			print(text.white)
		} catch let error {
			print("Failed to generate dark release asset".lightRed)
			print("\(error)".red)
		}
		print("")
		do {
			let text = try AssetGenerator.generate(from: "ReleaseLight.png")
			print("Release, light".bold.lightWhite)
			print(text.white)
		} catch let error {
			print("Failed to generate light release asset".lightRed)
			print("\(error)".red)
		}
		print("")
	}
	
}
